document.querySelector('#mrSwitch').addEventListener('click',function(){$.getJSON(mindreader.path,function(e){let s=document.querySelector('#mrSwitch');let t=document.querySelector('.mrtxt');let total=document.querySelector('.mrtotal');let now=parseInt(document.querySelector('.mrnow').innerHTML);let sum=e.summary;let rep=e.replies;if(!s.checked){total.innerHTML=sum.length;t.querySelector('.msg').innerHTML=sum[now-1]}else{total.innerHTML=rep.length;t.querySelector('.msg').innerHTML=rep[now-1]}})});function MRPageBack(dir){if(dir==='Left'){$.getJSON(mindreader.path,function(e){let s=document.querySelector('#mrSwitch');let t=document.querySelector('.mrtxt');let total=document.querySelector('.mrtotal');let now=document.querySelector('.mrnow');let sum=e.summary;let rep=e.replies;if(parseInt(now.innerHTML)>1){if(!s.checked){total.innerHTML=sum.length;now.innerHTML=parseInt(now.innerHTML)-1;t.querySelector('.msg').innerHTML=sum[parseInt(now.innerHTML)-1]}else{total.innerHTML=rep.length;now.innerHTML=parseInt(now.innerHTML)-1;t.querySelector('.msg').innerHTML=rep[parseInt(now.innerHTML)-1]}}})}else{$.getJSON(mindreader.path,function(e){let s=document.querySelector('#mrSwitch');let t=document.querySelector('.mrtxt');let total=document.querySelector('.mrtotal');let now=document.querySelector('.mrnow');let sum=e.summary;let rep=e.replies;if(parseInt(now.innerHTML)<parseInt(total.innerHTML)){if(!s.checked){total.innerHTML=sum.length;now.innerHTML=parseInt(now.innerHTML)+1;t.querySelector('.msg').innerHTML=sum[parseInt(now.innerHTML)-1]}else{total.innerHTML=rep.length;now.innerHTML=parseInt(now.innerHTML)+1;t.querySelector('.msg').innerHTML=rep[parseInt(now.innerHTML)-1]}}})}}function useRecommend(n){let str=n.parentElement.querySelector('.msg').innerText;let txtarea=document.querySelector('#content').value=str}