function toggleMic(elem){
	t = elem.getAttribute('mic-active');
	let txtarea = elem.parentElement.parentElement.querySelector('textarea');
	let recognition = new webkitSpeechRecognition();
	recognition.lang = window.navigator.language;
	recognition.interimResults = true;
	recognition.continuous = true;
	if(t==="true"){
		elem.setAttribute('mic-active', 'false');
		console.log('Stopped typing...');
		recognition.stop();
		elem.className = 'fa fa-microphone-slash';
	}else{
		elem.setAttribute('mic-active', 'true');
		console.log('Started typing...');
		recognition.start();
		elem.className = 'fa fa-microphone';
	}
	recognition.addEventListener('result', (event) => {
		const result = event.results[event.results.length - 1][0].transcript;
		if(elem.getAttribute('mic-active')==="true")
			txtarea.textContent = result;
	});
}